mapper.select:




		select device_id deviceId,ys_tds ysTds,cs_tds csTds,js_total jsTotal,jhs_size jhsSize,cjs_size cjsSize,ys_size ysSize,zs_time zsTime,create_date createDate ,mon,rank from (
select tmp.device_id, tmp.ys_tds, tmp.cs_tds, tmp.js_total, tmp.jhs_size, tmp.cjs_size, tmp.ys_size, tmp.zs_time, tmp.create_date ,tmp.mon,@rownum:=@rownum+1 ,
if(@mon=tmp.mon,@rank:=@rank+1,@rank:=1) as rank,
@mon:=tmp.mon
from (
select device_id, ys_tds, cs_tds, js_total, jhs_size, cjs_size, ys_size, zs_time, date_format(create_date, '%Y%m')  mon , create_date from device_data_stat
 WHERE device_id=#{deviceId} order BY create_date desc
) tmp ,(select @rownum :=0 , @mon := null ,@rank:=0) a ) RESULT WHERE RANK=1 order BY create_date desc





		select device_id deviceId,ys_tds ysTds,cs_tds csTds,js_total jsTotal,jhs_size jhsSize,cjs_size cjsSize,ys_size ysSize,zs_time zsTime,create_date createDate ,year,rank from (
select tmp.device_id, tmp.ys_tds, tmp.cs_tds, tmp.js_total, tmp.jhs_size, tmp.cjs_size, tmp.ys_size, tmp.zs_time, tmp.create_date ,tmp.year,@rownum:=@rownum+1 ,
if(@year=tmp.year,@rank:=@rank+1,@rank:=1) as rank,
@year:=tmp.year
from (
select device_id, ys_tds, cs_tds, js_total, jhs_size, cjs_size, ys_size, zs_time, date_format(create_date, '%Y')  year , create_date from device_data_stat
 WHERE device_id=#{deviceId} order BY create_date desc
) tmp ,(select @rownum :=0 , @year := null ,@rank:=0) a ) RESULT WHERE RANK=1 order BY create_date desc
